import React, {useState, useEffect} from 'react';
import styled from "styled-components"
import FitaCassete from './components/FitaCassete';
import axios from 'axios'
import MenuIcon from '@material-ui/icons/Menu';



const AppContainer = styled.div `
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  position: relative;
  padding-right:400px;
`

const DivPlaylist = styled.div`

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  max-height: 100vh;
  min-height: max-content;
  width: ${props => props.open? '350px': '86px'};
  box-shadow: 10px 20px 50px rgba(0,0,0,0.2);
  overflow: hidden;
  overflow-y: scroll;
`
const BurgerOpen = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 74px;
  height: 64px;
  width: 64px;
`

const TrackDiv = styled.div`
  display: flex;
  align-items: center;
  margin: auto;
  width: 320px;
  height:70px ;
  cursor: pointer;
  transition: 3s ease;
  &:hover{
    background-color:grey ;
  }
`
const AlbumImage = styled.img`
  border-radius: 50%;
  margin: 3px;
  animation:spin linear 4s infinite;
    animation-play-state: ${props=>props.animationPlay? 'running': 'paused'};
    @keyframes spin { 
      100% { -webkit-transform: rotate(360deg); transform:rotate(360deg); } 
    }
`

const NameTrack = styled.h3`
    font-family: 'Caveat', cursive;
    color: #1B1B1F;
    margin: auto;
    margin-left:5px ;
`
const AudioTrack = styled.audio`
   filter: invert(100);
   background-color: black;
   position: absolute;
   bottom: 10px;
   width: 80%;
   left:10%;
   outline: none;


`


const App = () => {

  const [tracks, setTracks]= useState([])
  const [trackName, setTrackName] = useState('Awesome Playlist')
  const [artistName, setArtistName] = useState('')
  const [menu, setMenu] = useState(false)
  const [previewUrl, setPreview] = useState('')
  const [animationPlay, setAnimationPlay] = useState(false)

  const getTracks = ()=>{
    const urlSpotify = 'https://api.spotify.com/v1/me/tracks'
    axios.get(urlSpotify, {
      headers: {
        Authorization: 'Bearer BQA6X-c0S67OeVNqO3DX5K9R0znIJHv7uthG8tM0CCIKKjsOQXSr0ATjJAztmf3KghbhOpSwypaUF4EB0p6nmbQO6PNaifm84-O2MK4TrM50_R2U7bJMCz_ticI3_eAWBXvBBo4cQq6q3ZkAHaxroJXpjlBSuGGUm0U9gGJNWwuF4jWbcE2GmFcFfqUTmnSi4aWm-ywOChT-SChRx4UUDZOw9AIuyngCt4qsPpHla0c2MKZ6O_J7gDZH7lVzjiriuNVE-yOYQiMgPhSG8ZLi2d8UB09uycAL3dk' 
      }
    }).then(response=>{

      const arrayTracks = response.data.items.map(item=>{
        return {
          getTrack: item.track.href,
          trackName: item.track.name,
          preview: item.track.preview_url,
          artistName: item.track.artists[0].name,
          getArtist: item.track.artists[0].href,
          albumImage: item.track.album.images[2].url,
          getAlbum: item.track.album.href,
          albumName: item.track.album.name,
          animationPlay: false
        }
      })
      setTracks(arrayTracks)
      console.log(arrayTracks)
    }).catch(error=>{
      console.log(error.message)
    })
  }
  useEffect(()=>{
    getTracks()
  }, [])

  const playtrack = (preview, trackName, artist)=>{

    const newTracks = tracks.map(track=>{
      if(track.trackName === trackName){
        track.animationPlay = true
      }else{
        track.animationPlay = false
      }
      return track
    })
    setTracks(newTracks)
    setTrackName(trackName)
    setArtistName(artist)
    setPreview(preview)
    console.log(preview)

  }


  return (
    <AppContainer>
      <DivPlaylist open={menu} >
        <TrackDiv>
        <BurgerOpen onClick={()=> setMenu(!menu) }>
          <MenuIcon fontSize='enherit'/>
        </BurgerOpen>
        <NameTrack>Awesome Playlists</NameTrack>
        </TrackDiv>
        {tracks && tracks.map(track=>{
          return <TrackDiv 
                  key={track.trackName}
                  onClick={()=>playtrack(track.preview, track.trackName, track.artistName)}>
            <AlbumImage animationPlay={track.animationPlay} src={track.albumImage}/>
            <NameTrack>{track.trackName} by: {track.artistName}</NameTrack>
          </TrackDiv>
        })}
      </DivPlaylist>
     <FitaCassete animationPlay={animationPlay} songTitle={trackName + ' by ' + artistName}/>
    {previewUrl && <AudioTrack
      onPlay={()=> setAnimationPlay(true)}
      onPause={()=>{setAnimationPlay(false)}}
      controls
      src={previewUrl}>
          Your browser does not support the
          <code>AudioTrack</code> element.
  </AudioTrack>}

    </AppContainer>
  );
}


export default App;
