import React, {useState, useEffect} from 'react';
import styled from "styled-components"
import FitaCassete from './components/FitaCassete';
import axios from 'axios'
import MenuIcon from '@material-ui/icons/Menu';



const AppContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`

const DivPlaylist = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  left: 0px;
  top: 0px;
  width: 350px ${props => prop};
  box-shadow: 10px 20px 50px rgba(0,0,0,0.2);
  overflow: hidden;
`
const BurgerOpen = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 74px;
  height: 64px;
  width: 64px;
`

const TrackDiv = styled.div`
  display: flex;
  align-items: center;
  margin: auto;
  width: 320px;
  height:70px ;
  cursor: pointer;
  transition: 1s ease;
  &:hover{
    background-color:grey ;
  }
`
const AlbumImage = styled.img`
  border-radius: 5px;
`

const NameTrack = styled.h3`
    font-family: 'Caveat', cursive;
    color: #1B1B1F;
    margin: auto;
`


const App = () => {

  const [tracks, setTracks]= useState([])
  const [trackName, setTrackName] = useState('Awesome Playlist')
  const [artistName, setArtistName] = useState('')

  const getTracks = ()=>{
    const urlSpotify = 'https://api.spotify.com/v1/me/tracks'
    axios.get(urlSpotify, {
      headers: {
        Authorization: 'Bearer BQB6KyHGudmc9I5RbfFVZog3-sP8lUKdowtm7OMRv1xbz6HqUJ6obVzxeW4-SJjg6Ao9iIppJXWlw_biNaMeF6vvX0Iik0lUB6a9uaxgUM1b7LZ9Rj0BYGTIcPs0wCv_dYc5BrQu0MzoM6Tqrufq1ithWeaCAhpQ1dDDwUb7yP75Vm2iwZMGamfOrzZKF99mhOuXNDt2RmoFj34nn2ZetJSI_hxuAz8CJwu0YF530mJeLwdFCo7n3LH0dz6BqqojpYEy8FKCSJaajj8XKEQVh4YSi43jmEQ8TfA' 
      }
    }).then(response=>{

      const arrayTracks = response.data.items.map(item=>{
        return {
          getTrack: item.track.href,
          trackName: item.track.name,
          preview: item.track.preview_url,
          artistName: item.track.artists[0].name,
          getArtist: item.track.artists[0].href,
          albumImage: item.track.album.images[2].url,
          getAlbum: item.track.album.href,
          albumName: item.track.album.name
        }
      })
      setTracks(arrayTracks)
      console.log(arrayTracks)
    }).catch(error=>{
      console.log(error.message)
    })
  }
  useEffect(()=>{
    getTracks()
  }, [])

  const playtrack = (preview, track, artist)=>{

    setTrackName(track)
    setArtistName(artist)

  }


  return (
    <AppContainer>
      <DivPlaylist >
        <TrackDiv>
        <BurgerOpen>
          <MenuIcon fontSize='enherit'/>
        </BurgerOpen>
        </TrackDiv>
        {tracks && tracks.map(track=>{
          return <TrackDiv 
                  key={track.trackName}
                  onClick={()=>playtrack(track.preview, track.trackName, track.artistName)}>
            <AlbumImage src={track.albumImage}/>
            <NameTrack>{track.trackName} by: {track.artistName}</NameTrack>
          </TrackDiv>
        })}
      </DivPlaylist>
     <FitaCassete songTitle={trackName + ' by ' + artistName}/>

    </AppContainer>
  );
}


export default App;
