import React, {useState, useEffect} from 'react';
import styled from "styled-components"
import FitaCassete from './components/FitaCassete';
import axios from 'axios'
import MenuIcon from '@material-ui/icons/Menu';



const AppContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`

const DivPlaylist = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  left: 5px;
  top: 0px;
  max-height: 100vh;
  min-
  width: ${props => props.open? '350px': '66px'};
  box-shadow: 10px 20px 50px rgba(0,0,0,0.2);
  overflow: hidden;
  overflow-y: scroll;
`
const BurgerOpen = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 74px;
  height: 64px;
  width: 64px;
`

const TrackDiv = styled.div`
  display: flex;
  align-items: center;
  margin: auto;
  width: 320px;
  height:70px ;
  cursor: pointer;
  transition: 3s ease;
  &:hover{
    background-color:grey ;
  }
`
const AlbumImage = styled.img`
  border-radius: 5px;
`

const NameTrack = styled.h3`
    font-family: 'Caveat', cursive;
    color: #1B1B1F;
    margin: auto;
    margin-left:5px ;
`


const App = () => {

  const [tracks, setTracks]= useState([])
  const [trackName, setTrackName] = useState('Awesome Playlist')
  const [artistName, setArtistName] = useState('')
  const [menu, setMenu] = useState(false)

  const getTracks = ()=>{
    const urlSpotify = 'https://api.spotify.com/v1/me/tracks'
    axios.get(urlSpotify, {
      headers: {
        Authorization: 'Bearer BQAugkPFAy2H0qirHg1nTZz0gB-0xdYdG9ClOPiezunZ9JZknUON62gg-Xt-ZC4TdbYa2Rc3UicNS8B-4WuPhb0-wlIi32lXyKfQ4lLvJe7HNE140f7gPTxQftPL-KIDGgRttvwa4r0CrXgHPIJ6YE6I7KKl7hcCIz3fE-h5hjuq_KTHiX77KlodmRB8MpTlu1KiZCFzH_1wAHL2GqiYY0LYyedhAYsh5hc3VQPa0EBkgErQFLWKK_a-B4TJ7-pWinBg6k74PwO6R_1AVHCZPhgmoeu8z9ETGEQ' 
      }
    }).then(response=>{

      const arrayTracks = response.data.items.map(item=>{
        return {
          getTrack: item.track.href,
          trackName: item.track.name,
          preview: item.track.preview_url,
          artistName: item.track.artists[0].name,
          getArtist: item.track.artists[0].href,
          albumImage: item.track.album.images[2].url,
          getAlbum: item.track.album.href,
          albumName: item.track.album.name
        }
      })
      setTracks(arrayTracks)
      console.log(arrayTracks)
    }).catch(error=>{
      console.log(error.message)
    })
  }
  useEffect(()=>{
    getTracks()
  }, [])

  const playtrack = (preview, track, artist)=>{

    setTrackName(track)
    setArtistName(artist)

  }


  return (
    <AppContainer>
      <DivPlaylist open={menu} >
        <TrackDiv>
        <BurgerOpen onClick={()=> setMenu(!menu) }>
          <MenuIcon fontSize='enherit'/>
        </BurgerOpen>
        </TrackDiv>
        {tracks && tracks.map(track=>{
          return <TrackDiv 
                  key={track.trackName}
                  onClick={()=>playtrack(track.preview, track.trackName, track.artistName)}>
            <AlbumImage src={track.albumImage}/>
            <NameTrack>{track.trackName} by: {track.artistName}</NameTrack>
          </TrackDiv>
        })}
      </DivPlaylist>
     <FitaCassete songTitle={trackName + ' by ' + artistName}/>

    </AppContainer>
  );
}


export default App;
